import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import axios from 'axios'



const ProfileItem = ({
  profile: {
    user: { _id, name},
    status,
    company,
    location,
    skills,
    githubusername
  }
}) => {
  const [data, setData] = useState({imageURL : ''});

  useEffect(() => {
    const fetchData = async () => {
